mapper.select:
SELECT * 
FROM GATEWAY_API_DEFINE a
WHERE a.id = #{id}

mapper.update:
UPDATE SYS_APP a SET
	a.`name` = #{name},
	a.`code` = #{code},
	a.sort = #{sort},
	a.isEnable = #{isEnable}
WHERE a.`id` = #{id}

UPDATE SYS_APP a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM SYS_APP


		WHERE 1 != 1


		WHERE `id` = #{id}


		WHERE `id` in #{id}
