(function($) {
	$.permission = function() {
        var data = $("#_permission").attr("data");
        if(!data){
            data="";
		}
		$("[permission]").each(function(j,d) {if($.inArray($(d).attr("permission"), data.split(","))>-1){$(d).hide();}});
	};
})(jQuery);